import React, { Component } from 'react';
import Header from './Header';
import styled from 'styled-components';
import Input from './input';
import BlueButton from './BlueButton';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import UserContext from './UserContext';

const Container = styled.div`
  padding: 30px 20px;
`;

class SignupPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      redirectToHomepage: false,
    };
  }

  signup() {
    axios
      .post(
        'http://localhost:3001/api/signup', // ðŸ” Adjust if your backend uses a different prefix
        {
          email: this.state.email,
          password: this.state.password,
        },
        { withCredentials: true }
      )
      .then(() => {
        this.setState({ redirectToHomepage: true });
      })
      .catch((error) => {
        console.error('Signup error:', error);
        alert(
          'Signup failed: ' +
            (error.response?.data?.message || 'Unknown error')
        );
      });
  }

  render() {
    if (this.state.redirectToHomepage) {
      return <Navigate to="/" />;
    }

    return (
      <Container>
        <Header style={{ marginBottom: '20px' }}>Signup</Header>
        <Input
          placeholder="email"
          type="email"
          value={this.state.email}
          onChange={(e) => this.setState({ email: e.target.value })}
        />
        <Input
          placeholder="password"
          type="password"
          autoComplete="new-password"
          value={this.state.password}
          onChange={(e) => this.setState({ password: e.target.value })}
        />
        <BlueButton onClick={() => this.signup()}>Signup</BlueButton>
      </Container>
    );
  }
}

SignupPage.contextType = UserContext;

export default SignupPage;
