import React, { Component } from 'react';
import Header from './Header';
import styled from 'styled-components';
import Input from './input';
import BlueButton from './BlueButton';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import UserContext from './UserContext';


const Container = styled.div`
padding: 30px 20px;
`;


class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        }

    axios.post('http://localhost:3001/login', {
        email: this.state.email,
        password: this.state.password,
      }, {
        withCredentials: true,
      })
      .then(() => {
        this.context.checkAuth().then(() => {
          this.setState({ redirectToHomepage: true });
        });
      })
      .catch(err => {
        console.error(err);
      });


    }
    render() {
        return (<>
        {this.state.redirectToHomepage && (
            <Navigate to={'/'} />
        )}
        <Container>
            <Header style={{maarginBottom: '20px'}}>Login</Header>
            <Input placeholder={'email'} type="email" value={this.state.email}
            onChange={e => this.setState({email:e.target.value})} />
            <Input placeholder={'password'} type="password" value={this.state.password}
            onChange={e => this.setState({password:e.target.value})} />
            <BlueButton onClick={() => this.login()}>Login</BlueButton>
        </Container>
        </>);
    }
}

LoginPage.contextType = UserContext;

export default LoginPage;
