import React, { Component } from 'react';
import Header from './Header';
import styled from 'styled-components';
import Input from './input';
import BlueButton from './BlueButton';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import UserContext from './UserContext';


const Container = styled.div`
padding: 30px 20px;
`;


class SignupPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
        }
    }

    signup() {
        axios.post( 'http://localhost:3001/signup', {
            email: this.email,
            password: this.password,
        }, {withCredentials: true})

    }
    render() {
        return (<>
        {this.state.redirectToHomepage && (
            <Navigate to={'/'} />
        )}
        <Container>
            <Header style={{maarginBottom: '20px'}}>Signup</Header>
            <Input placeholder={'email'} type="email" value={this.email}
            onChange={e => this.setState({email:e.target.value})} />
            <Input placeholder={'password'} type="password" value={this.password}
            autoComplete='new-password'
            onChange={e => this.setState({password:e.target.value})} />
            <BlueButton onClick={() => this.signup()}>Signup</BlueButton>
        </Container>
        </>);
    }
}

SignupPage.contextType = UserContext;

export default SignupPage;
