import styled from 'styled-components';
import Input from './input';
import AskBlueButton from './AskBlueButton';
import React from 'react';
import Markdown from 'https://esm.sh/react-markdown@10'
import gfn from 'remark-gfm';
import { useState } from 'react';
import { initialState } from 'react';
import axios from 'axios';





const PreviewQuestion = styled.div`
padding: 20px;
background-color: grey;
border-radius: 5px;
margin-bottom: 20px;
`;





const QuestionBodyInput = styled.textarea`
background: none;
border: 1px solid grey;
border-radius: 3px;
display: block;
width: 100%;
box-sizing: border-box;
padding: 10px;
min-height: 300px;
margin-bottom: 20px; 
color: black;  
`;






function AskPage() {

    const [questionTitle, setQuestionTitle] = useState(initialState, '');
    const [questionBody, setQuestionBody] = useState(initialState, '');

    function question() {
        axios.post('http://localhost:3001/question', {
            title: questionTitle,
            content: questionBody,
        }, {withCreditials: true})
        .theb(response => console.log(response))
    }

    db.select('token')
    .from('users')
    .where({token})
        .first()
        .then( onfullfilled, user => {
            if (user && user.id) {
                db('posts').insert({
                    title, 
                    content,
                    parent_id: null,
                    user_id: 0,
                }).then( onfullfilled, () => {
                    response.sendStatus(201);
                }).catch(() => response.sendStatus(422));
                } else {
                    response.sendStatus(403);
                }
                })


    function question() {
        Event.preventDefault();
        axios.post('http://localhost:3001/questions', {
            title: questionTitle,
            content: questionBody,
        }, {withCredentials: true})
        .then(response => console.log(response));
    }


    return (
        <main>
            <h1>Ask a Question</h1>
        <Input type="text" 
            value={questionTitle} 
            onChange={e => setQuestionTitle(e.target.value)}
            placeholder="Title of your question" />
            <QuestionBodyInput 
                onChange={e => setQuestionBody(e.target.value)}
                placeholder="Question?">{questionBody}</QuestionBodyInput>
            <PreviewQuestion>
                <Markdown>
                    plugins={{gfn}} children={questionBody}
                 </Markdown>
            </PreviewQuestion>
            <AskBlueButton style={{marginTop:'20px'}}>Submit</AskBlueButton>


        </main>




    );
}


export default AskPage;
